import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      const result = await axios.get('http://localhost:5000/tasks');
      setTasks(result.data);
    };
    fetchTasks();
  }, []);

  const addTask = async () => {
    const result = await axios.post('http://localhost:5000/tasks', { text: taskText, completed: false });
    setTasks([...tasks, result.data]);
    setTaskText('');
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    setTasks(tasks.filter(task => task._id !== id));
  };

  const startEditing = (task) => {
    setEditId(task._id);
    setEditText(task.text);
  };

  const editTask = async () => {
    await axios.put(`http://localhost:5000/tasks/${editId}`, { text: editText });
    setTasks(tasks.map(task => (task._id === editId ? { ...task, text: editText } : task)));
    setEditId(null);
    setEditText('');
  };

  const toggleCompletion = async (task) => {
    await axios.put(`http://localhost:5000/tasks/${task._id}`, { ...task, completed: !task.completed });
    setTasks(tasks.map(t => (t._id === task._id ? { ...t, completed: !t.completed } : t)));
  };

  return (
    <Container className="mt-5">
      <h1 className="text-center">To-Do List</h1>
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={taskText}
            onChange={(e) => setTaskText(e.target.value)}
            placeholder="Enter new task"
          />
        </Col>
        <Col>
          <Button variant="primary" onClick={addTask}>Add Task</Button>
        </Col>
      </Row>
      <ul className="list-unstyled">
        {tasks.map(task => (
          <li key={task._id} className="d-flex justify-content-between align-items-center mb-2">
            <Form.Check
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleCompletion(task)}
            />
            {editId === task._id ? (
              <div className="d-flex">
                <Form.Control
                  type="text"
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                />
                <Button variant="success" onClick={editTask} className="ml-2">Save</Button>
              </div>
            ) : (
              <>
                <span>{task.text}</span>
                <div>
                  <Button variant="warning" onClick={() => startEditing(task)} className="mr-2">Edit</Button>
                  <Button variant="danger" onClick={() => deleteTask(task)}>Delete</Button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </Container>
  );
};

export default App;
