import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [taskName, setTaskName] = useState('');
  const [taskDescription, setTaskDescription] = useState('');
  const [taskStartTime, setTaskStartTime] = useState('');
  const [taskEndDate, setTaskEndDate] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      const result = await axios.get('http://localhost:5000/tasks');
      setTasks(result.data);
    };
    fetchTasks();
  }, []);

  const addTask = async () => {
    const result = await axios.post('http://localhost:5000/tasks', {
      name: taskName,
      description: taskDescription,
      startTime: taskStartTime,
      endDate: taskEndDate,
      completed: false
    });
    setTasks([...tasks, result.data]);
    setTaskName('');
    setTaskDescription('');
    setTaskStartTime('');
    setTaskEndDate('');
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    setTasks(tasks.filter(task => task._id !== id));
  };

  const startEditing = (task) => {
    setEditId(task._id);
    setEditText({
      name: task.name,
      description: task.description,
      startTime: task.startTime,
      endDate: task.endDate,
    });
  };

  const editTask = async () => {
    await axios.put(`http://localhost:5000/tasks/${editId}`, {
      ...editText,
      completed: false,
    });
    setTasks(tasks.map(task => (task._id === editId ? { ...task, ...editText } : task)));
    setEditId(null);
    setEditText('');
  };

  const toggleCompletion = async (task) => {
    await axios.put(`http://localhost:5000/tasks/${task._id}`, { ...task, completed: !task.completed });
    setTasks(tasks.map(t => (t._id === task._id ? { ...t, completed: !t.completed } : t)));
  };

  return (
    <Container className="mt-5">
      <h1 className="text-center">To-Do List</h1>
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
            placeholder="Task Name"
          />
        </Col>
        <Col>
          <Form.Control
            type="text"
            value={taskDescription}
            onChange={(e) => setTaskDescription(e.target.value)}
            placeholder="Task Description"
          />
        </Col>
        <Col>
          <Form.Control
            type="datetime-local"
            value={taskStartTime}
            onChange={(e) => setTaskStartTime(e.target.value)}
            placeholder="Start Time"
          />
        </Col>
        <Col>
          <Form.Control
            type="date"
            value={taskEndDate}
            onChange={(e) => setTaskEndDate(e.target.value)}
            placeholder="End Date"
          />
        </Col>
        <Col>
          <Button variant="primary" onClick={addTask}>Add Task</Button>
        </Col>
      </Row>
      <ul className="list-unstyled">
        {tasks.map(task => (
          <li key={task._id} className="d-flex justify-content-between align-items-center mb-2">
            <Form.Check
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleCompletion(task)}
            />
            {editId === task._id ? (
              <div className="d-flex">
                <Form.Control
                  type="text"
                  value={editText.name}
                  onChange={(e) => setEditText({ ...editText, name: e.target.value })}
                  placeholder="Task Name"
                />
                <Form.Control
                  type="text"
                  value={editText.description}
                  onChange={(e) => setEditText({ ...editText, description: e.target.value })}
                  placeholder="Task Description"
                />
                <Form.Control
                  type="datetime-local"
                  value={editText.startTime}
                  onChange={(e) => setEditText({ ...editText, startTime: e.target.value })}
                  placeholder="Start Time"
                />
                <Form.Control
                  type="date"
                  value={editText.endDate}
                  onChange={(e) => setEditText({ ...editText, endDate: e.target.value })}
                  placeholder="End Date"
                />
                <Button variant="success" onClick={editTask} className="ml-2">Save</Button>
              </div>
            ) : (
              <>
                <div>
                  <strong>{task.name}</strong> - {task.description}<br/>
                  Start: {new Date(task.startTime).toLocaleString()}<br/>
                  End: {new Date(task.endDate).toLocaleDateString()}
                </div>
                <div>
                  <Button variant="warning" onClick={() => startEditing(task)} className="mr-2">Edit</Button>
                  <Button variant="danger" onClick={() => deleteTask(task._id)}>Delete</Button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </Container>
  );
};

export default App;
